class NumberOnlyTextEdit(QtWidgets.QTextEdit):
    def keyPressEvent(self, event):
        key = event.key()
        text = event.text()
        
        # Allow only numeric keys, plus, minus, backspace, and delete
        if key in [Qt.Key_0, Qt.Key_1, Qt.Key_2, Qt.Key_3, Qt.Key_4,
                   Qt.Key_5, Qt.Key_6, Qt.Key_7, Qt.Key_8, Qt.Key_9,
                   Qt.Key_Plus, Qt.Key_Minus,
                   Qt.Key_Backspace, Qt.Key_Delete, Qt.Key_Left, Qt.Key_Right]:
            super().keyPressEvent(event)
        elif text.isdigit() or text in ['+', '-', '.']:
            super().keyPressEvent(event)
        else:
            event.ignore()

