                self.measure_display.insertPlainText(". Second point at: ("+str(int((self.res_fast/self.view_width)*pos.x()))+","+str(int((1024/self.view_height)*(self.view_height-pos.y())))+")")
                self.measure_display.append("Direct distance measured: "+str(np.round(measured_dist,2))+" micron")
                self.measure_display.append("Vertical distance measured: "+str(np.abs(np.round(measured_y,2)))+" micron")
                self.measure_display.append("Horizontal distance measured: "+str(np.abs(np.round(measured_x,2)))+" micron")
                self.addItem(scatter)
                pen = pg.mkPen(width=2,color='r')
                self.plot([self.position1[0],self.position1[0]],[self.position1[1],self.position2[1]],pen=pen)
                self.plot([self.position1[0],self.position2[0]],[self.position2[1],self.position2[1]],pen=pen)
                self.plot([self.position1[0],self.position2[0]],[self.position1[1],self.position2[1]],pen=pen)
                row = [np.abs(np.round(measured_y,2)),np.abs(np.round(measured_x,2)),np.round(measured_dist,2),self.bscan_number,self.measurement_note]
                self.dm_table.write_to_table(row)

                if self.multiple_measurement:
                    number_text = self.dm_table.get_cur_row_num()-1
                    measurement_number = pg.TextItem(text=f"{number_text}",color=(255,0,0))
                    measurement_number.setFont(QFont("Arial",10))
