class HoverButton(QPushButton):
    def __init__(self, parent=None):
        super().__init__(parent)

        # Set initial background color
        self.setStyleSheet("background-color: rgb(50, 50, 50); color: white; border: none;")

        # Connect hover events

    def enterEvent(self, event):
        # Change background color on hover
        if self.isEnabled():
            self.setStyleSheet("background-color: rgb(150, 150, 150); color: white; border: none;")

    def leaveEvent(self, event):
        # Revert to initial background color when not hovered
        if self.isEnabled():
            self.setStyleSheet("background-color: rgb(50, 50, 50); color: white; border: none;")

    def setEnabled(self, enabled):
        # Override setEnabled method to adjust opacity when disabled
        super().setEnabled(enabled)
        if enabled:
            self.setStyleSheet("background-color: rgb(50, 50, 50); color: white; border: none;")
        else:
            self.setStyleSheet("background-color: rgba(0, 0, 0, 0.5); color: white; border: none;")

